mapper.select:
select id

ORDER BY lcd DESC
LIMIT #{startNum}, #{pageSize}

select `id`, `name`, `display_name` as displayName, `type`, `value`, `description`, `fcu`, `fcd`, `lcu`, `lcd` from `autoexec_global_param` where `id` in
#{item}
ORDER BY `lcd` DESC

select count(id)
from autoexec_global_param
where id != #{id}
  and name =#{name}

select count(id)
from autoexec_global_param
where id != #{id}
  and display_name =#{displayName}
